#define QUEUE_DATA_TYPE int
const int QUEUE_SIZE=1000000;struct QUEUE{QUEUE_DATA_TYPE q[QUEUE_SIZE];int head,tail,num;QUEUE(){head=0;tail=-1;num=0;return;}void push(QUEUE_DATA_TYPE in){tail=tail+1<QUEUE_SIZE?tail+1:0;q[tail]=in;++num;return;}void pop(){head=head+1<QUEUE_SIZE?head+1:0;--num;return;}void push_front(QUEUE_DATA_TYPE in){head=(~(head-1))?head-1:QUEUE_SIZE-1;q[head]=in;++num;return;}void pop_back(){tail=(~(tail-1))?tail-1:QUEUE_SIZE-1;--num;return;}QUEUE_DATA_TYPE front(){return q[head];}QUEUE_DATA_TYPE back(){return q[tail];}void clear(){head=0;tail=-1;num=0;return;}bool empty(){return!num;}int size(){return num;}QUEUE_DATA_TYPE read(int in){return q[(head+in)%QUEUE_SIZE];}};